import React, {useState} from "react";
import { Link as RouterLink } from "react-router-dom";
import validator from 'validator';
import "../styles/signup.css";
import RegexPassword from "../utils";



function Signup(){
    const [values, setValues] = useState({
        email:'',
        password:'',
        repeatPassword:'',
        showPassword: false,
        showRepeatPassword: false,
    })

    const [errors, setErrors] = useState({
        email: false,
        password: false,
        repeatPassword:false,
        fetchError: false,
        fetchErrorMsg: '',
    })

    const handleChange = (fieldName) => (e) =>{
        const userValue = e.target.value
        switch(fieldName){
            case 'email':
                validator.isEmail(userValue)
                  ? setErrors({ ...errors, email: false })
                  : setErrors({ ...errors, password: true })
                break
            case 'password':
                RegexPassword(userValue)
                  ? setErrors({ ...errors, password: false })
                  : setErrors({ ...errors, password: true})
                break
            case 'repeatPassword':
                userValue === values.password
                  ? setErrors({ ...errors, repeatPassword: false })
                  : setErrors({ ...errors, repeatPassword:true })
                break
        }
        setValues({ ...values, [fieldName]: e.target.value })
    }

    const handleShowPassword = (showPasswordField) => {
        setValues({
            ...values,
            [showPasswordField]:!values[showPasswordField],
        })
    }

    const handleSubmit = async(e) => {
        e.preventDefault()
        try {
            const res = await fetch('/api/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: values.email,
                    password: values.password
                })
            })
            if(!res.ok){
                const error = await res.json()
                return setErrors({
                    ...errors,
                    fetchError: true,
                    fetchErrorMsg: error.msg,
                })
            }
            const data = await res.json()
            setErrors({
                ...errors,
                fetchError: true,
                fetchErrorMsg: data.msg
            })
            setValues({
                email:'',
                password:'',
                repeatPassword:'',
                showPassword: false,
                showRepeatPassword: false,
            })
            return 
        } 
        catch(error){
            setErrors({
                ...errors,
                fetchError: true,
                fetchErrorMsg: 'There was a problem with our server, please try again later',
            })
        }
    } 

    return (
       <>
            <div className="container">
                <div className="card mx-auto shadow-sm">
                    <h5 className="card-header">สมัครสมาชิก</h5>
                    <div class="card-body">
                        <form>
                        <div class="form-group">
                                <label for="exampleInputEmail1">Email address</label>
                                <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                            <div class="form-group">
                                <label for="exampleInputEmail1">Email address</label>
                                <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                            <div class="form-group">
                                <label for="exampleInputPassword1">Password</label>
                                <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password" />
                            </div>
                            <div class="form-group form-check">
                                <input type="checkbox" class="form-check-input" id="exampleCheck1" />
                                <label class="form-check-label" for="exampleCheck1">Check me out</label>
                            </div>
                            <button type="submit" class="btn btn-primary">Submit</button>
                        </form>
                    </div>  
                </div>
            </div>
       </>
    )
}


export default Signup
