import React, {useState} from "react";
import { Link as RouterLink } from "react-router-dom";
import validator from 'validator';
import theme from "../styles/theme";
import RegexPassword from "../utils";



function Signup(){
    const [values, setValues] = useState({
        email:'',
        password:'',
        repeatPassword:'',
        showPassword: false,
        showRepeatPassword: false,
    })

    const [errors, setErrors] = useState({
        email: false,
        password: false,
        repeatPassword:false,
        fetchError: false,
        fetchErrorMsg: '',
    })

    const handleChange = (fieldName) => (e) =>{
        const userValue = e.target.value
        switch(fieldName){
            case 'email':
                validator.isEmail(userValue)
                  ? setErrors({ ...errors, email: false })
                  : setErrors({ ...errors, password: true })
                break
            case 'password':
                RegexPassword(userValue)
                  ? setErrors({ ...errors, password: false })
                  : setErrors({ ...errors, password: true})
                break
            case 'repeatPassword':
                userValue === values.password
                  ? setErrors({ ...errors, repeatPassword: false })
                  : setErrors({ ...errors, repeatPassword:true })
                break
        }
        setValues({ ...values, [fieldName]: e.target.value })
    }

    const handleShowPassword = (showPasswordField) => {
        setValues({
            ...values,
            [showPasswordField]:!values[showPasswordField],
        })
    }

    const handleSubmit = async(e) => {
        e.preventDefault()
        try {
            const res = await fetch('/api/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: values.email,
                    password: values.password
                })
            })
            if(!res.ok){
                const error = await res.json()
                return setErrors({
                    ...errors,
                    fetchError: true,
                    fetchErrorMsg: error.msg,
                })
            }
            const data = await res.json()
            setErrors({
                ...errors,
                fetchError: true,
                fetchErrorMsg: data.msg
            })
            setValues({
                email:'',
                password:'',
                repeatPassword:'',
                showPassword: false,
                showRepeatPassword: false,
            })
            return 
        } 
        catch(error){
            setErrors({
                ...errors,
                fetchError: true,
                fetchErrorMsg: 'There was a problem with our server, please try again later',
            })
        }
    } 

    return (
        // <>
        //     <Container sx={{ marginTop: 'calc(100vh - 45%)'}} maxWidth='sm'>
        //         <Paper elevation={6}>
        //             <Container maxWidth='sm' sx={{ display:'flex', flexDirection:'column', justifyContent:'center', alignItems:'center', paddingTop: '20px'}}>
        //                 <h2>สมัครสมาชิก</h2>
        //             </Container>
        //             <Stack component='form' onSubmit={handleSubmit} noValidate spacing={6}>

        //             </Stack>

        //         </Paper>
        //     </Container>
        // </>
        <ThemeProvider theme={theme}>
            <Container component="main" maxWidth="xs">
                <CssBaseline />
                <Box
                sx={{
                    marginTop: 8,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                }}
                >
                <Typography component="h1" variant="h5">
                    สมัครสมาชิก
                </Typography>
                <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
                    {/* <Grid container spacing={2}> */}
                    <Typography component="h5" variant="h5">
                    สมัครสมาชิก
                    </Typography>
                    <FormControl variant="standard">
                        <InputLabel shrink htmlFor="bootstrap-input">
                        Bootstrap
                        </InputLabel>
                        <BootstrapInput defaultValue="react-bootstrap" id="bootstrap-input" />
                    </FormControl>
                    {/* <Grid item xs={12}>
                        <TextField
                        autoComplete="given-name"
                        name="firstName"
                        required
                        fullWidth
                        id="firstName"
                        label="ชื่อ-นามสกุล"
                        autoFocus
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                        required
                        fullWidth
                        id="email"
                        label="อีเมล"
                        name="email"
                        autoComplete="email"
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                        required
                        fullWidth
                        name="password"
                        label="รหัสผ่าน"
                        type="password"
                        id="password"
                        autoComplete="new-password"
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <FormControlLabel
                        control={<Checkbox value="allowExtraEmails" color="primary" />}
                        label="I want to receive inspiration, marketing promotions and updates via email."
                        />
                    </Grid>
                    </Grid> */}
                    <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    sx={{ mt: 3, mb: 2, bgcolor:'#388ED0' }}
                    >
                    Sign Up
                    </Button>
                    <Grid container justifyContent="flex-end">
                    <Grid item>
                        <Link href="#" variant="body2">
                        Already have an account? Sign in
                        </Link>
                    </Grid>
                    </Grid>
                </Box>
                </Box>
            </Container>
        </ThemeProvider>
    )
}


