import React, {useState} from "react";
import { Link as RouterLink } from "react-router-dom";
import validator from 'validator';
import theme from "../styles/theme";
import RegexPassword from "../utils";
import {
    Paper,
    Container,
    Link,
    Stack,
    Button,
    Box,
    Divider,
    Avatar,
    Typography,
    TextField,
    FilledInput,
    InputAdornment,
    IconButton,
    InputLabel,
    FormControl,
    FormHelperText,
} from '@mui/material';

function Signup(){
    const [values, setValues] = useState({
        email:'',
        password:'',
        repeatPassword:'',
        showPassword: false,
        showRepeatPassword: false,
    })

    const [errors, setErrors] = useState({
        email: false,
        password: false,
        repeatPassword:false,
        fetchError: false,
        fetchErrorMsg: '',
    })

    const handleChange = (fieldName) => (e) =>{
        const userValue = e.target.value
        switch(fieldName){
            case 'email':
                validator.isEmail(userValue)
                  ? setErrors({ ...errors, email: false })
                  : setErrors({ ...errors, password: true })
                break
            case 'password':
                RegexPassword(userValue)
                  ? setErrors({ ...errors, password: false })
                  : setErrors({ ...errors, password: true})
                break
            case 'repeatPassword':
                userValue === values.password
                  ? setErrors({ ...errors, repeatPassword: false })
                  : setErrors({ ...errors, repeatPassword:true })
                break
        }
        setValues({ ...values, [fieldName]: e.target.value })
    }

    const handleShowPassword = (showPasswordField) => {
        setValues({
            ...values,
            [showPasswordField]:!values[showPasswordField],
        })
    }

    const handleSubmit = async(e) => {
        e.preventDefault()
        try {
            const res = await fetch('/api/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: values.email,
                    password: values.password
                })
            })
            if(!res.ok){
                const error = await res.json()
                return setErrors({
                    ...errors,
                    fetchError: true,
                    fetchErrorMsg: error.msg,
                })
            }
            const data = await res.json()
            setErrors({
                ...errors,
                fetchError: true,
                fetchErrorMsg: data.msg
            })
            setValues({
                email:'',
                password:'',
                repeatPassword:'',
                showPassword: false,
                showRepeatPassword: false,
            })
            return 
        } 
        catch(error){
            setErrors({
                ...errors,
                fetchError: true,
                fetchErrorMsg: 'There was a problem with our server, please try again later',
            })
        }
    } 

    return (
        <>
            <Container sx={{ marginTop: 'calc(100vh - 45%)'}} maxWidth='sm'>
                <Paper elevation={6}>
                    <Container maxWidth='sm' sx={{ display:'flex'}}>

                    </Container>
                </Paper>
            </Container>
        </>
    )
}
